// chat_interaction.js

// Function to create a new message element
function createMessage(text, sender) {
    const messageDiv = document.createElement('div');
    messageDiv.classList.add('message');
    messageDiv.classList.add(sender);
    messageDiv.textContent = text;
    return messageDiv;
}

// Function to add a message to the chat area
function addMessageToChat(message, sender, chatAreaId) {
    const chatArea = document.getElementById(chatAreaId);
    const messageElement = createMessage(message, sender);
    chatArea.appendChild(messageElement);
    chatArea.scrollTop = chatArea.scrollHeight; // Keep scrolling to the bottom
}

// Simulate LLM responses
function simulateChat(chatAreaId) {
    const llmResponses = [
        "Hello! How can I assist you today?",
        "That's an interesting question. Let me think...",
        "Here's some information I found on the topic.",
        "Is there anything else I can help with?",
        "Goodbye! Have a great day."
    ];

    const userQueries = [
        "Hello",
        "What is the capital of France",
        "Tell me a joke",
        "Thank you",
        "Bye"
    ];

    let messageIndex = 0;

    function sendMessage() {
        if (messageIndex < llmResponses.length) {
            //add user query
            addMessageToChat(userQueries[messageIndex], 'user', chatAreaId);
            // Add LLM response after a short delay
            setTimeout(() => {
                addMessageToChat(llmResponses[messageIndex], 'llm', chatAreaId);
                messageIndex++;
                if (messageIndex < llmResponses.length){
                     setTimeout(sendMessage, 1000); // Send the next message after 1 second
                }

            }, 500); // Simulate a 0.5-second delay for LLM response
        }
    }

    sendMessage();
}

// Function to attach the simulateChat function to a button click
function attachChatSimulation(buttonId, chatAreaId) {
    const button = document.getElementById(buttonId);
    button.addEventListener('click', () => {
        simulateChat(chatAreaId);
    });
}


