import json

urls = inputs.get('previous_output', {}).get('xuv_3xo_feature_urls', [])

feature_data = []

for item in urls:
    url = item.get('url', '')
    content = item.get('content', '')

    if url and content:
        # Extracting data based on identified patterns from previous data
        details = {
            'url': url,
            'features': []
        }

        if 'cardekho' in url:
            if 'Soft touch material on dashboard' in content:
                details['features'].append('Soft touch material on dashboard')
            if 'Panoramic sunroof' in content:
                details['features'].append('Panoramic sunroof')
            if 'Level 2 ADAS' in content:
                details['features'].append('Level 2 ADAS')
            if 'turbo-petrol engine makes 130PS/230Nm' in content:
                details['features'].append('1.2-litre turbo-petrol engine makes 130PS/230Nm')
            details['engine_displacement'] = content.split('Engine Displacement ')[1].split(' cc ')[0] if 'Engine Displacement' in content else None
            details['max_power'] = content.split('Max Power ')[1].split('bhp')[0] if 'Max Power' in content else None

        elif 'spinny' in url:
             if 'Versatile Powertrain Options' in content:
                 details['features'].append('Versatile Powertrain Options')
             if 'Advanced Safety Features' in content:
                 details['features'].append('Advanced Safety Features')           
             if 'Premium Interior and Comfort' in content:
                 details['features'].append('Premium Interior and Comfort')
             if 'Modern Exterior Design' in content:
                 details['features'].append('Modern Exterior Design')             

        elif 'autocarindia' in url:
            if 'Level 2 ADAS' in content:
                 details['features'].append('Level 2 ADAS')
            if 'panoramic sunroof which is the widest in the segment' in content:
                 details['features'].append('panoramic sunroof which is the widest in the segment')
            if '60 connected car features' in content:
                 details['features'].append('60 connected car features')            

        feature_data.append(details)


output = {'xuv_3xo_feature_details': feature_data}

