import json

def extract_features(previous_output):
    feature_list = {
        'engine': [],
        'safety': [],
        'interior': [],
        'exterior': [],
        'technology': []
    }

    all_results = previous_output.get('xuv_3xo_features', [])

    for result in all_results:
        content = result.get('content', '')

        if not content:
            continue

        # Engine features
        if 'engine' in content.lower() or 'power' in content.lower() or 'torque' in content.lower() or 'displacement' in content.lower():
            feature_list['engine'].append(content)

        # Safety features
        if 'safety' in content.lower() or 'abs' in content.lower() or 'adas' in content.lower() or 'airbag' in content.lower() or 'collision' in content.lower():
            feature_list['safety'].append(content)

        # Interior features
        if 'interior' in content.lower() or 'seat' in content.lower() or 'climate control' in content.lower() or 'sunroof' in content.lower():
            feature_list['interior'].append(content)

        # Exterior features
        if 'exterior' in content.lower() or 'wheel' in content.lower() or 'dimensions' in content.lower() or 'ground clearance' in content.lower():
            feature_list['exterior'].append(content)

        # Technology features
        if 'technology' in content.lower() or 'connected car' in content.lower() or 'touchscreen' in content.lower() or 'infotainment' in content.lower():
            feature_list['technology'].append(content)

    return feature_list


