files.style.css:
body {
    font-family: Arial, sans-serif;
}

.container {
    width: 300px;
}

.cell {
    width: 80px;
    height: 80px;
    text-align: center;
    vertical-align: middle;
    font-size: 2em;
    cursor: pointer;
}

.border {
    border: 1px solid #ccc;
}

.border-l {
    border-left: 1px solid #ccc;
}

.border-r {
    border-right: 1px solid #ccc;
}

.border-t {
    border-top: 1px solid #ccc;
}

.border-b {
    border-bottom: 1px solid #ccc;
}

.cell:hover {
    background-color: #f0f0f0;
}

