def extract_asian_paints_data(data):
    asian_paints_data = []
    for item in data:
        if 'content' in item and item['content']:
            content = item['content']
            url = item.get('url', 'No URL provided')

            # Extracting Growth Drivers
            if 'growth' in content.lower() or 'increasing demand' in content.lower():
                growth_drivers = content
            else:
                growth_drivers = 'Not explicitly mentioned in content'

            # Extracting Challenges
            if 'challenge' in content.lower() or 'volatility' in content.lower():
                challenges = content
            else:
                challenges = 'Not explicitly mentioned in content'

            # Extracting Opportunities
            if 'opportunity' in content.lower():
                opportunities = content
            else:
                opportunities = 'Not explicitly mentioned in content'

            # Extracting Regulatory Landscape
            if 'regulation' in content.lower() or 'VOC emission' in content.lower():
                regulatory_landscape = content
            else:
                regulatory_landscape = 'Not explicitly mentioned in content'

            asian_paints_data.append({
                'url': url,
                'growth_drivers': growth_drivers,
                'challenges': challenges,
                'opportunities': opportunities,
                'regulatory_landscape': regulatory_landscape
            })
    return asian_paints_data

