        labels: ['2022', '2023', '2024'],
        datasets: [{
            label: 'Revenue (Billions USD)',
            data: [81.46, 96.77, 97.69],
            backgroundColor: 'rgba(54, 162, 235, 0.7)',
            borderColor: 'rgba(54, 162, 235, 1)',
            borderWidth: 1
        }]
